mapper.select:
select m.movie_id as movieId,m.down_href as downHref,m.movie_name as movieName,m.category as category,m.synchronous_flag as synchronousFlag, m.source as source,m.img_url as imgUrl,m.img_url2 as imgUrl2,m.describes as describes,m.insert_date as insertDate,m.remarks as remarks from movie m where 1=1
and category =#{category}
order by m.insert_date
LIMIT ${(currentPage-1)*pageSize}, ${pageSize}

mapper.update:
update movie set synchronous_flag = 'Y',category = '2',update_date = now()
where movie_id in

#{item}
